import { useEffect, useState } from "react";
import Gitems from "./GroceryList";
import "./App.css"
export default function Grocery() {
  const [name,sname ] = useState("Name");
  const [age ,sage ] = useState("Age");
  const [dd,sadd ] = useState("");
  const [sal,ssal ] = useState("");
  
  const [allitem, new___add] = useState([]);
useEffect(()=>{
  fetch("http://localhost:3000/List")
  .then((x)=>x.json())
  .then((x)=>new___add(x))
},[])
  const add = () => {
    
   let  obj=  {
      
      "name":name,
      "age":age,
      "add":dd,
      "salary":sal

      
    }
    console.log(obj)

    fetch("http://localhost:3000/List",{
      method:"POST",
      body:JSON.stringify(obj),
      headers:{
        "Content-Type":"application/json"
      }
    })

    new___add([...allitem,obj]);
    
  
  };

  const remove = (i_n) => {
    console.log(allitem[i_n]);
    fetch(`http://localhost:3000/List/${i_n}`,{
      method:"DELETE",
      
      headers:{
        "Content-Type":"application/json"
      }
    })

    
  };

  return (
    <> <div className="main">
      <h1>Grocery_List</h1>
      <input
        type="text"
        value={name}
        onChange={(e) => sname(e.target.value)}
      />
       <input
        type="number"
        value={age}
        onChange={(e) => sage(e.target.value)}
      />
      
      <input
        type="text"
        value={dd}
        onChange={(e) => sadd(e.target.value)}
      />
       <input
        type="number"
        value={sal}
        onChange={(e) => ssal(e.target.value)}
      />      

      <button className="button-30" onClick={add}>Add Item</button>
       {allitem.map((x, i) => {
        return <Gitems item={x.task} id={x.id}  key={i} remove={remove} />;
      })}  

      </div>
    </>
  );
}
