import { useEffect, useState } from "react";
import Sh from "./showtask";
export default function Use_effect() {
  const [task, setask] = useState("");
  const [alltask, newtask] = useState(["ddd"]);
useEffect(()=>{
  fetch("http://localhost:3000/todo")
  .then((x)=>x.json())
  .then((x)=>newtask(x))
},[alltask])
  const add = () => {
    
   let  obj=  {
      
      "task": task,
      "status": "false"
    }

    fetch("http://localhost:3000/todo",{
      method:"POST",
      body:JSON.stringify(obj),
      headers:{
        "Content-Type":"application/json"
      }
    })

    newtask([...alltask,task]);
    console.log(alltask);
    setask("");
  };

  const remove = (index) => {
    
    var sanjana = alltask.filter((value, k) => {
      return index !== k;
    });
    newtask(sanjana);
  };

  return (
    <>
      <h1>TO DO</h1>
      <input
        type="text"
        value={task}
        onChange={(e) => setask(e.target.value)}
      />
      <button onClick={add}>Add task</button>
       {alltask.map((x, i) => {
        return <Sh item={x.task} id={i}  key={i} remove={remove} />;
      })} 
    </>
  );
}
