import { useEffect, useState } from "react";
import Gitems from "./GroceryList";
import "./App.css"
import { findAllByTestId } from "@testing-library/react";
export default function Grocery() {
  const [name,sname ] = useState("");
  const [age ,sage ] = useState("");
  const [dd,sadd ] = useState("");
  const [sal,ssal ] = useState("");
  const [married,sm ] = useState(false);
  
  const [allitem, new___add] = useState([]);
useEffect(()=>{
  fetch("http://localhost:3000/List")
  .then((x)=>x.json())
  .then((x)=>new___add(x))
},[])
  const add = () => {
    
   let  obj=  {
      
      "name":name,
      "age":age,
      "add":dd,
      "salary":sal,
      "married":married

      
    }
    console.log(obj)

    fetch("http://localhost:3000/List",{
      method:"POST",
      body:JSON.stringify(obj),
      headers:{
        "Content-Type":"application/json"
      }
    })

    new___add([...allitem,obj]);
    
  
  };

  const remove = (i_n) => {
    console.log(allitem[i_n]);
    fetch(`http://localhost:3000/List/${i_n}`,{
      method:"DELETE",
      
      headers:{
        "Content-Type":"application/json"
      }
    })

    
  };

  return (
    <> <div className="main">
      <h1>Regstration</h1>
    <h1>Name</h1>
      <input
      placeholder="Name"
        type="text"
        value={name}
        onChange={(e) => sname(e.target.value)}
      />
      <h1>Age</h1>
       <input
       placeholder="Age"
        type="number"
        value={age}
        onChange={(e) => sage(e.target.value)}
      />
      <h1>Address:</h1>
      <input
        type="text"
        value={dd}
        onChange={(e) => sadd(e.target.value)}
      />
      <h1>Salary</h1>
       <input
        type="number"
        value={sal}
        onChange={(e) => ssal(e.target.value)}
      />     
      <h1>Mark if married</h1> 
      <input
        type="checkbox"
        checked={married}
        onChange={() => married?sm(false):sm(true)}
      />   
<br/>
      <button className="button-30" onClick={add}>Add Emplo</button>
       {allitem.map((x, i) => {
        return <Gitems item={x.task} id={x.id}  key={i} remove={remove} />;
      })}  

      </div>
    </>
  );
}
